// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title SilicaModelRegistry
 * @dev Registry for AI models in the Silica ecosystem
 */
contract SilicaModelRegistry is AccessControl, ReentrancyGuard {
    using SafeERC20 for IERC20;
    
    bytes32 public constant MODEL_CREATOR_ROLE = keccak256("MODEL_CREATOR_ROLE");
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    
    // Structs
    struct ModelMetadata {
        uint256 id;
        address creator;
        string name;
        string description;
        string modelType; // e.g., "text-generation", "image-generation", "classification"
        string version;
        string storageURI; // URI pointing to model weights (IPFS, Arweave, etc.)
        string apiEndpoint; // Optional API endpoint for hosted models
        bool isActive;
        uint256 createdAt;
        uint256 updatedAt;
        uint256 usageCount;
        uint256 feePerCall; // Fee in wei per API call
    }
    
    struct ModelUsageStats {
        uint256 totalCalls;
        uint256 totalRevenue;
        uint256 lastUsedTimestamp;
    }
    
    // State variables
    uint256 private _modelIdCounter;
    mapping(uint256 => ModelMetadata) public models;
    mapping(uint256 => ModelUsageStats) public modelStats;
    mapping(address => uint256[]) public creatorModels;
    mapping(string => bool) public modelNameExists;
    
    // Events
    event ModelRegistered(uint256 indexed modelId, address indexed creator, string name, string modelType);
    event ModelUpdated(uint256 indexed modelId, string version, string storageURI);
    event ModelStatusChanged(uint256 indexed modelId, bool isActive);
    event ModelUsed(uint256 indexed modelId, address indexed user, uint256 fee);
    event FeeUpdated(uint256 indexed modelId, uint256 newFee);
    
    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(MODEL_CREATOR_ROLE, msg.sender);
    }
    
    /**
     * @dev Register a new AI model
     * @param name Model name (must be unique)
     * @param description Brief description of the model
     * @param modelType Type of AI model
     * @param version Initial version of the model
     * @param storageURI URI where model weights are stored
     * @param apiEndpoint Optional API endpoint for the model
     * @param feePerCall Fee to charge per model call (in wei)
     * @return The ID of the newly registered model
     */
    function registerModel(
        string calldata name,
        string calldata description,
        string calldata modelType,
        string calldata version,
        string calldata storageURI,
        string calldata apiEndpoint,
        uint256 feePerCall
    ) external onlyRole(MODEL_CREATOR_ROLE) returns (uint256) {
        require(bytes(name).length > 0, "Model name cannot be empty");
        require(bytes(storageURI).length > 0, "Storage URI cannot be empty");
        require(!modelNameExists[name], "Model name already exists");
        
        uint256 modelId = _modelIdCounter++;
        
        models[modelId] = ModelMetadata({
            id: modelId,
            creator: msg.sender,
            name: name,
            description: description,
            modelType: modelType,
            version: version,
            storageURI: storageURI,
            apiEndpoint: apiEndpoint,
            isActive: true,
            createdAt: block.timestamp,
            updatedAt: block.timestamp,
            usageCount: 0,
            feePerCall: feePerCall
        });
        
        modelStats[modelId] = ModelUsageStats({
            totalCalls: 0,
            totalRevenue: 0,
            lastUsedTimestamp: 0
        });
        
        creatorModels[msg.sender].push(modelId);
        modelNameExists[name] = true;
        
        emit ModelRegistered(modelId, msg.sender, name, modelType);
        
        return modelId;
    }
    
    /**
     * @dev Update an existing model
     * @param modelId ID of the model to update
     * @param version New version of the model
     * @param storageURI New URI where model weights are stored
     * @param apiEndpoint New API endpoint for the model
     */
    function updateModel(
        uint256 modelId,
        string calldata version,
        string calldata storageURI,
        string calldata apiEndpoint
    ) external {
        ModelMetadata storage model = models[modelId];
        require(model.creator == msg.sender || hasRole(ADMIN_ROLE, msg.sender), "Not authorized");
        require(model.id == modelId, "Model does not exist");
        
        model.version = version;
        model.storageURI = storageURI;
        model.apiEndpoint = apiEndpoint;
        model.updatedAt = block.timestamp;
        
        emit ModelUpdated(modelId, version, storageURI);
    }
    
    /**
     * @dev Toggle the active status of a model
     * @param modelId ID of the model
     * @param isActive New active status
     */
    function setModelStatus(uint256 modelId, bool isActive) external {
        ModelMetadata storage model = models[modelId];
        require(model.creator == msg.sender || hasRole(ADMIN_ROLE, msg.sender), "Not authorized");
        require(model.id == modelId, "Model does not exist");
        
        model.isActive = isActive;
        
        emit ModelStatusChanged(modelId, isActive);
    }
    
    /**
     * @dev Update the fee for using a model
     * @param modelId ID of the model
     * @param newFee New fee amount in wei
     */
    function updateModelFee(uint256 modelId, uint256 newFee) external {
        ModelMetadata storage model = models[modelId];
        require(model.creator == msg.sender || hasRole(ADMIN_ROLE, msg.sender), "Not authorized");
        require(model.id == modelId, "Model does not exist");
        
        model.feePerCall = newFee;
        
        emit FeeUpdated(modelId, newFee);
    }
    
    /**
     * @dev Record usage of a model and collect fee
     * @param modelId ID of the model being used
     * @param user Address of the user using the model
     * @param _paymentToken Address of token used for payment (can be zero address for ETH)
     */
    function recordModelUsage(uint256 modelId, address user, address _paymentToken) external nonReentrant {
        ModelMetadata storage model = models[modelId];
        require(model.id == modelId, "Model does not exist");
        require(model.isActive, "Model is not active");
        
        // Update usage statistics
        model.usageCount++;
        
        ModelUsageStats storage stats = modelStats[modelId];
        stats.totalCalls++;
        stats.totalRevenue += model.feePerCall;
        stats.lastUsedTimestamp = block.timestamp;
        
        emit ModelUsed(modelId, user, model.feePerCall);
    }
    
    /**
     * @dev Get a model's metadata
     * @param modelId ID of the model
     * @return The model metadata
     */
    function getModel(uint256 modelId) external view returns (ModelMetadata memory) {
        require(models[modelId].id == modelId, "Model does not exist");
        return models[modelId];
    }
    
    /**
     * @dev Get models created by a specific address
     * @param creator Address of the creator
     * @return Array of model IDs created by the address
     */
    function getModelsByCreator(address creator) external view returns (uint256[] memory) {
        return creatorModels[creator];
    }
    
    /**
     * @dev Get total models count
     * @return The total number of models registered
     */
    function getModelCount() external view returns (uint256) {
        return _modelIdCounter;
    }
    
    /**
     * @dev Grant MODEL_CREATOR_ROLE to an address
     * @param account Address to grant the role to
     */
    function addModelCreator(address account) external onlyRole(ADMIN_ROLE) {
        _grantRole(MODEL_CREATOR_ROLE, account);
    }
    
    /**
     * @dev Revoke MODEL_CREATOR_ROLE from an address
     * @param account Address to revoke the role from
     */
    function removeModelCreator(address account) external onlyRole(ADMIN_ROLE) {
        _revokeRole(MODEL_CREATOR_ROLE, account);
    }
}

contracts/SilicaToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/draft-ERC20Permit.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Votes.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SilicaToken
 * @dev ERC20 token with governance capabilities and AI control
 */
contract SilicaToken is ERC20, ERC20Permit, ERC20Votes, AccessControl, Ownable {
    uint256 public constant INITIAL_SUPPLY = 100000000 * 10**18; // 100 million tokens with 18 decimals
    bytes32 public constant AI_CONTROLLER_ROLE = keccak256("AI_CONTROLLER_ROLE");
    
    uint256 public maxSupply = 1000000000 * 10**18; // 1 billion max supply
    
    event TokensMinted(address to, uint256 amount);
    event TokensBurned(address from, uint256 amount);
    
    constructor() 
        ERC20("Silica", "SIL") 
        ERC20Permit("Silica")
    {
        _mint(msg.sender, INITIAL_SUPPLY);
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(AI_CONTROLLER_ROLE, msg.sender);
    }
    
    /**
     * @dev Allows the owner to mint new tokens
     * @param to The address to mint tokens to
     * @param amount The amount of tokens to mint
     */
    function mint(address to, uint256 amount) external onlyOwner {
        require(totalSupply() + amount <= maxSupply, "Exceeds max supply");
        _mint(to, amount);
        emit TokensMinted(to, amount);
    }
    
    /**
     * @dev Allows the AI controller to mint new tokens
     * @param to The address to mint tokens to
     * @param amount The amount of tokens to mint
     */
    function aiMint(address to, uint256 amount) external onlyRole(AI_CONTROLLER_ROLE) {
        require(totalSupply() + amount <= maxSupply, "Exceeds max supply");
        _mint(to, amount);
        emit TokensMinted(to, amount);
    }
    
    /**
     * @dev Allows the AI controller to burn tokens
     * @param from The address to burn tokens from
     * @param amount The amount of tokens to burn
     */
    function aiBurn(address from, uint256 amount) external onlyRole(AI_CONTROLLER_ROLE) {
        _burn(from, amount);
        emit TokensBurned(from, amount);
    }
    
    /**
     * @dev Allows the AI controller to adjust the max supply
     * @param newMaxSupply The new maximum supply
     */
    function setMaxSupply(uint256 newMaxSupply) external onlyRole(AI_CONTROLLER_ROLE) {
        require(newMaxSupply >= totalSupply(), "New max supply below current total");
        maxSupply = newMaxSupply;
    }
    
    /**
     * @dev Grant AI controller role to an address
     * @param controller The address to grant the role to
     */
    function addAIController(address controller) external onlyOwner {
        grantRole(AI_CONTROLLER_ROLE, controller);
    }
    
    /**
     * @dev Revoke AI controller role from an address
     * @param controller The address to revoke the role from
     */
    function removeAIController(address controller) external onlyOwner {
        revokeRole(AI_CONTROLLER_ROLE, controller);
    }
    
    /**
     * @dev See {ERC20-_mint}.
     */
    function _mint(address account, uint256 amount) internal virtual override(ERC20, ERC20Votes) {
        super._mint(account, amount);
    }

    /**
     * @dev See {ERC20-_burn}.
     */
    function _burn(address account, uint256 amount) internal virtual override(ERC20, ERC20Votes) {
        super._burn(account, amount);
    }

    /**
     * @dev See {ERC20-_afterTokenTransfer}. Moves vote power when tokens are transferred.
     */
    function _afterTokenTransfer(address from, address to, uint256 amount) internal virtual override(ERC20, ERC20Votes) {
        super._afterTokenTransfer(from, to, amount);
    }

    /**
     * @dev Returns whether `interfaceId` is supported by this contract.
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override(AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

contracts/SilicaTreasury.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title SilicaTreasury
 * @dev Treasury contract for the Silica DAO that manages funds
 */
contract SilicaTreasury is AccessControl {
    using SafeERC20 for IERC20;
    
    bytes32 public constant GOVERNOR_ROLE = keccak256("GOVERNOR_ROLE");
    bytes32 public constant AI_CONTROLLER_ROLE = keccak256("AI_CONTROLLER_ROLE");
    
    event FundsDeposited(address indexed token, address indexed from, uint256 amount);
    event FundsWithdrawn(address indexed token, address indexed to, uint256 amount);
    event AssetAdded(address indexed token, string name, string assetType);
    event InvestmentExecuted(address indexed targetProtocol, address indexed token, uint256 amount, bytes data);
    
    // Asset tracking
    struct Asset {
        string name;
        string assetType; // "stablecoin", "governance", "yield", etc.
        bool isActive;
    }
    
    mapping(address => Asset) public assets;
    address[] public assetList;
    
    constructor(address governor) {
        _grantRole(DEFAULT_ADMIN_ROLE, governor);
        _grantRole(GOVERNOR_ROLE, governor);
        
        // Also grant the role to the deployer for testing purposes
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(GOVERNOR_ROLE, msg.sender);
    }
    
    /**
     * @dev Deposits tokens into the treasury
     * @param token The ERC20 token to deposit
     * @param amount The amount to deposit
     */
    function depositFunds(address token, uint256 amount) external {
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        emit FundsDeposited(token, msg.sender, amount);
    }
    
    /**
     * @dev Withdraws tokens from the treasury
     * @param token The ERC20 token to withdraw
     * @param to The recipient address
     * @param amount The amount to withdraw
     */
    function withdrawFunds(address token, address to, uint256 amount) external onlyRole(GOVERNOR_ROLE) {
        IERC20(token).safeTransfer(to, amount);
        emit FundsWithdrawn(token, to, amount);
    }
    
    /**
     * @dev Register a new asset for tracking
     * @param token The token address
     * @param name The name of the asset
     * @param assetType The type of asset
     */
    function addAsset(address token, string calldata name, string calldata assetType) 
        external
        onlyRole(GOVERNOR_ROLE)
    {
        require(!assets[token].isActive, "Asset already exists");
        
        assets[token] = Asset({
            name: name,
            assetType: assetType,
            isActive: true
        });
        
        assetList.push(token);
        emit AssetAdded(token, name, assetType);
    }
    
    /**
     * @dev Grant AI controller role to an address
     * @param controller The AI controller address
     */
    function setAIController(address controller) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(AI_CONTROLLER_ROLE, controller);
        _grantRole(GOVERNOR_ROLE, controller);
    }
    
    /**
     * @dev Execute an investment strategy through an external protocol
     * @param targetProtocol Protocol to interact with (e.g., lending platform)
     * @param token Token to invest
     * @param amount Amount to invest
     * @param data Encoded function call data
     */
    function executeInvestment(
        address targetProtocol,
        address token,
        uint256 amount,
        bytes calldata data
    ) 
        external
        onlyRole(AI_CONTROLLER_ROLE)
        returns (bool success)
    {
        // Approve token usage
        IERC20(token).approve(targetProtocol, amount);
        
        // Execute the investment transaction
        (success, ) = targetProtocol.call(data);
        require(success, "Investment execution failed");
        
        // Reset approval
        IERC20(token).approve(targetProtocol, 0);
        
        emit InvestmentExecuted(targetProtocol, token, amount, data);
        return success;
    }
    
    /**
     * @dev Get token balance of the treasury
     * @param token The ERC20 token address
     * @return The balance of tokens held by the treasury
     */
    function getTokenBalance(address token) external view returns (uint256) {
        return IERC20(token).balanceOf(address(this));
    }
    
    /**
     * @dev Get list of all registered assets
     * @return Array of asset addresses
     */
    function getAllAssets() external view returns (address[] memory) {
        return assetList;
    }
    
    /**
     * @dev Receive ETH
     */
    receive() external payable {
        emit FundsDeposited(address(0), msg.sender, msg.value);
    }
}

